sys_script_include.script:
var createRecordAud = Class.create();
createRecordAud.prototype = {
    initialize: function() {
    },


	atualizarCreditos: function(solicitante, solicitacao, valor, quantidade) {


        var criarRegistroCredito = new GlideRecord("x_1328471_solici_0_auditoria");
        criarRegistroCredito.initialize();
        criarRegistroCredito.solicitante = solicitante;
		criarRegistroCredito.solicitacao_de_credito = solicitacao;
		criarRegistroCredito.valor_do_credito = valor;
        criarRegistroCredito.quantidade = quantidade;

        criarRegistroCredito.insert();

        return true;

	},

    type: 'createRecordAud'
};
