sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	//gs.info("**** Processando email ****\n" + email.subject + "\nBody: " + email.body_text );

	//gs.info('subject: ' + email.subject + ' | nome: ' + email.body.nome + ' | endereco: ' + email.body.endereco + ' | conta: ' + email.body.conta);


	//VERIFICA O VALOR DE CADA VARIÁVEL DO BODY E ATRIBUI EM SEU RESPECTIVO CAMPO

	if (email.body_text != undefined) {

       current.description = 'Body Text: \n\n' + email.body_text;

    }

	if (email.subject != undefined) {

        current.short_description = email.subject;

    }

    if (email.body.nome != undefined) {

        current.nome = email.body.nome;

    }

	if (email.body.telefone != undefined) {

        current.telefone = email.body.telefone;

    }

	if (email.body.email != undefined) {

       current.email = email.body.email;

    }

	if (email.body.sexo != undefined) {

        current.sexo = email.body.sexo;

    }

	if (email.body.cpf != undefined) {

        current.cpf = email.body.cpf;

    }

	if (email.body.rg != undefined) {

        current.rg = email.body.rg;

    }

	if (email.body.endereco != undefined) {

        current.endereco = email.body.endereco;

    }

	if (email.body.agencia != undefined) {

        current.agencia = email.body.agencia;

    }

	if (email.body.valor != undefined) {

        current.valor_do_credito = email.body.valor;

    }

	if (email.body.conta != undefined) {

        current.conta = email.body.conta;

    }

	if (email.body.quantidade_de_parcelas != undefined) {

        current.quantidade = email.body.quantidade_de_parcelas;

    }

 current.insert();

})(current, event, email, logger, classifier);
